mapper.select:
SELECT d.id, d.name, c.name AS categoryName, d.price, d.image, d.description, d.status, d.update_time FROM dish d LEFT JOIN category c ON d.category_id = c.id
AND d.name like concat('%',#{name},'%')
AND d.category_id = #{categoryId}
AND d.status = #{status}
ORDER BY d.update_time DESC
